def editDistance(string1, string2):
    if len(string1) == 0:
        return len(string2)
    if len(string2) == 0:
        return len(string1)
    a = 1 if string1[-1] != string2[-1] else 0

    b = editDistance(string1[:-1], string2[:-1]) + a

    c= editDistance(string1[:-1], string2) + 1

    d = editDistance(string1, string2[:-1]) + 1
    return min(b, c, d)

x=""
y = ""
print(editDistance(x,y))
